import React from 'react';
import '../../assets/Styles/Styles.css'
import FormValidator from '../Validator/FormValidator';
import axios from 'axios';
import toastr from 'toastr';
import env from "../../env";
import Navbar from "../navbar/Navbar";
import { MDBRow, MDBCol, MDBCardBody, MDBBtn, MDBContainer,  MDBCard, MDBModalFooter, MDBIcon, MDBCardHeader,
     } from "mdbreact";





export default class Login extends React.Component {

    constructor(props) {
        super(props);

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);


        this.validator = new FormValidator([

            {
                field: 'username',
                method: 'isEmpty',
                validWhen: false,
                message: 'Username is required'
            },
            {
                field: 'username',
                method: 'matches',
                args: [/^[a-z0-9A-Z\s]*$/],
                validWhen: true,
                message: 'Username can only contain letters and numbers'
            },
            {
                field: 'password',
                method: 'isEmpty',
                validWhen: false,
                message: 'Password is required'
            }
        ])

        this.state = {
            username: '',
            password: '',
            validation: this.validator.valid(),
            successmessage: '',
            errormessage: '',

        };
        this.submitted = false;
    }

    handleInputChange(event) {
        event.preventDefault()
        this.setState({ [event.target.name]: event.target.value });
    }

    handleSubmit = async (event) => {
        event.preventDefault();

        const validation = this.validator.validate(this.state);
        this.setState({ validation });
        this.submitted = true;

        if (validation.isValid) {
            try {
                const res = await axios.post(`${env.api}/users/login`, this.state);
                const token = res.data.data.token;

                localStorage.setItem('token', token);

                this.props.history.push('/create-post');
                console.log(res);
            } catch (err) {
                toastr.options.positionClass = "toast-top-center";
                toastr.error('Invalid Credentials, try again');
            }
        }
        else {
            toastr.options.positionClass = "toast-top-center";

            toastr.warning('Cannot Log In User Make sure all fields are correctly filled');
        }

    }

    componentDidMount() {
        const token = localStorage.getItem('token');

        if (token) return this.props.history.push('/login');
    }



    render() {
        let validation = this.submitted ?        // if the form has been submitted at least once
            this.validator.validate(this.state) :   // then check validity every time we render
            this.state.validation;
        return (
            <MDBContainer fluid >
                <Navbar />


                <div className=' logincontain '>

                    <div className='bgoverlay'>

                        <h2 className="err-success text-center">{this.state.errormessage}</h2>
                        <h2 className="err-success text-center">{this.state.successmessage}</h2>
                        <MDBContainer mt-3>
      <MDBRow>
        <MDBCol md="6">
          <MDBCard>
            <MDBCardBody>
              <MDBCardHeader className="form-header warm-flame-gradient rounded">
                <h3 className="my-3">
                  <MDBIcon icon="lock" /> Login:
                </h3>
              </MDBCardHeader>
              <label
                htmlFor="defaultFormEmailEx"
                className="grey-text font-weight-light"
              >
                Your email
              </label>
              <input
                type="email"
                id="defaultFormEmailEx"
                className="form-control"
              />

              <label
                htmlFor="defaultFormPasswordEx"
                className="grey-text font-weight-light"
              >
                Your password
              </label>
              <input
                type="password"
                id="defaultFormPasswordEx"
                className="form-control"
              />

              <div className="text-center mt-4">
                <MDBBtn color="deep-orange" className="mb-3" type="submit">
                  Login
                </MDBBtn>
              </div>

              <MDBModalFooter>
                <div className="font-weight-light">
                  <p>Not a member? Sign Up</p>
                  <p>Forgot Password?</p>
                </div>
              </MDBModalFooter>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
                        {/* <div className='row' >
                            <form>
                                <div className="form-group {validation.username.isInvalid && 'has-error'}">
                                    <label for="username">Username</label>
                                    <input type="text" className="form-control" id="username"
                                        placeholder="Username" name='username' value={this.state.username}
                                        onChange={this.handleInputChange} />
                                    <span className="help-block">{validation.username.message}</span>

                                </div>

                                <div className="form-group {validation.password.isInvalid && 'has-error'}">
                                    <label for="password">Password</label>
                                    <input type="password" className="form-control"
                                        id="password" placeholder="Password" name='password' value={this.state.password}
                                        onChange={this.handleInputChange} />

                                    <span className="help-block">{validation.password.message}</span>
                                </div>


                                <div>
                                    <button type="submit" onClick={this.handleSubmit} className="btn btn-success">Login</button>
                                    <span style={{ paddingLeft: 15 }}>Don't have an account? <a className="btn btn-link" href="/signup"> <button type="button" className="btn btn-dark" >
                                        SIGN UP
                    </button></a></span>


                                </div>

                            </form>

                        </div> */}


                    </div>
                </div>

            </MDBContainer>

        );
    }
}
