import React from 'react';
import '../../assets/Styles/Styles.css';
import FormValidator from '../Validator/FormValidator';
import axios from 'axios';
import toastr from 'toastr';
import env from "../../env";
import { MDBRow, MDBCol, MDBCard, MDBCardBody, MDBBtn, MDBContainer, MDBIcon } from "mdbreact";


export default class CreateBlog extends React.Component {
    constructor(props) {
        super(props);

        this.handleInputChange = this.handleInputChange.bind(this);
        this.handlePostSubmit = this.handleFormSubmit.bind(this);


        this.validator = new FormValidator([

            {
                field: 'title',
                method: 'isEmpty',
                validWhen: false,
                message: 'Title is required'
            },

            {
                field: 'body',
                method: 'isEmpty',
                validWhen: false,
                message: 'Body is required'
            },

        ]);

        this.state = {
            title: '',
            body: '',
            author: '',
            image_url: '',
            likes: '',
            validation: this.validator.valid(),
            successmessage: '',
            errormessage: '',
        }
    }

    handleInputChange(event) {
        event.preventDefault();
        this.setState({ [event.target.name]: event.target.value })

    }


    handleFormSubmit = async (event) => {
        event.preventDefault();
        const validation = this.validator.validate(this.state);
        this.setState({ validation });
        this.submitted = true;

        if (validation.isValid) {
            try {
                const token = localStorage.getItem('token');
                const headers = {
                    'auth-token': token
                };
                const { title, body, image_url } = this.state;
                const postData = {
                    title,
                    body,
                    image_url
                };
                console.log(postData)
                const res = await axios.post(`${env.api}/posts`, postData, { headers: headers });
                toastr.options.positionClass = "toast-top-center";

                if (res) {
                    setTimeout(() =>

                        toastr.success('New Post Successfully Created'), 2000)
                }
                this.props.history.push('/create-post');

            } catch (err) {
                toastr.error('An Error Occured, try again')

            }

        } else {
            toastr.options.positionClass = "toast-top-center"

            toastr.warning('Cannot Create Post Make sure all fields are correctly filled')

        }
    }

    uploadWidget(event) {
        const $this = this;
        event.preventDefault();
        window.cloudinary.openUploadWidget({ cloud_name: 'dxxm4ehaw', upload_preset: 'ruvxhuuh', tags: ['xmas'] },
            (error, result) => {
                console.log(result[0]);
                $this.setState({ image_url: result[0].url });
            });
    }
    render() {

        let validation = this.submitted ?           // if the form has been submitted at least once
            this.validator.validate(this.state) :   // then check validity every time we render
            this.state.validation
        return (
            <MDBContainer className="backgroundCP" fluid>

                <MDBContainer style={{backgroundColor:"white"}}>
                <MDBRow>
        <MDBCol md="12">
          <MDBCard>
            <MDBCardBody>
              <form>
                <p className="h4 text-center py-4">C</p>
                <label
                  htmlFor="defaultFormCardNameEx"
                  className="grey-text font-weight-light"
                >
                  Your name
                </label>
                <input
                  type="text"
                  id="defaultFormCardNameEx"
                  className="form-control"
                />
                <br />
                <label
                  htmlFor="defaultFormCardEmailEx"
                  className="grey-text font-weight-light"
                >
                  Your email
                </label>
                <input
                  type="email"
                  id="defaultFormCardEmailEx"
                  className="form-control"
                />
                <div className="text-center py-4 mt-3">
                  <MDBBtn className="btn btn-outline-purple" type="submit">
                    Send
                    <MDBIcon far icon="paper-plane" className="ml-2" />
                  </MDBBtn>
                </div>
              </form>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
                </MDBContainer>

            </MDBContainer>
            // <div className='container createpostcontainer'>
            //     <h2 className="err-success text-center">{this.state.errormessage}</h2>
            //     <h2 className="err-success text-center">{this.state.successmessage}</h2>
            //     <div className='row createpostbody'>
            //         <form>
            //             <div className=' form-group uploadimg' >
            //                 <button onClick={this.uploadWidget.bind(this)} className="upload-button btn btn-dark">
            //                    Click here to add image
            //         </button>

            //             </div>

            //             <div className='form-group title'>
            //                 <input className='titletext' name='title'
            //                     placeholder='Enter your Title' value={this.state.title}
            //                     onChange={this.handleInputChange} />

            //                 <span className="help-block">{validation.title.message}</span>

            //             </div>

            //             <div className='form-group body'>
            //                 <textarea className='bodytext' name='body'
            //                     onChange={this.handleInputChange} value={this.state.body} placeholder='Create your Story' >

            //                 </textarea>
            //                 <span className="help-block">{validation.body.message}</span>

            //             </div>

            //             <div className='form-group uploadbtn'>
            //                 <button type="submit" onClick={this.handleFormSubmit}
            //                     className="btn btn-success">Create</button>

            //                     <a class="btn btn-danger cancelbtn" href="/" role="button">Cancel</a>
            //             </div>
            //         </form>

            //     </div>

            // </div>
        )
    }
}
