import React from 'react';
import '../../assets/Styles/Styles.css';
import FormValidator from '../Validator/FormValidator';
import axios from 'axios';
import toastr from 'toastr';
import env from "../../env";
import bloglogo from '../../assets/Images/bloglogo.png';
import Navbar from "../navbar/Navbar"



export default class SignUp extends React.Component {

    constructor(props) {
        super(props);


        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

        this.validator = new FormValidator([

            {
                field: 'username',
                method: 'isEmpty',
                validWhen: false,
                message: 'Username is required'
            },
            {
                field: 'username',
                method: 'matches',
                args: [/^[a-z0-9A-Z\s]*$/],
                validWhen: true,
                message: 'Username can only contain letters and numbers'
            },
            {
                field: 'password',
                method: 'isEmpty',
                validWhen: false,
                message: 'Password is required'
            }
        ])

        this.state = {
            username:'',
            password:'',
            validation: this.validator.valid(),
            successmessage: '',
            errormessage: '',

        };
        this.submitted = false;
    }


    handleInputChange(event) {
        this.setState({ [event.target.name]: event.target.value });
    }

    handleSubmit = async (event) => {
        event.preventDefault();
        const validation = this.validator.validate(this.state);
        this.setState({ validation });
        this.submitted = true;

        if (validation.isValid) {

            //handle actual form submission here

            try {
                const res = await axios.post(`${env.api}/users`, this.state);
                const token = res.data.data.token;

                localStorage.setItem('token', token);

                this.props.history.push('/create-post');
            } catch (err) {
                toastr.error(err.response);
                console.log('An Error Occured', err.response);
            }


        }
        else {
            toastr.options.positionClass = "toast-top-center";

            toastr.warning('Cannot Create User Make sure all fields are correctly filled');

        }
    };
    componentDidMount() {
        const token = localStorage.getItem('token');

        if (token) return this.props.history.push('/signup');
    }


    render() {
        let validation = this.submitted ?            // if the form has been submitted at least once
            this.validator.validate(this.state) :   // then check validity every time we render
            this.state.validation;
        return (
            <div className='container-fluid logincontain'>

             <div className='bgoverlay'>
                <h3 >Sign Up</h3>
                <h2 className="err-success text-center">{this.state.errormessage}</h2>
                <h2 className="err-success text-center">{this.state.successmessage}</h2>

                <div className='row' >
                    <form >
                        <div className="form-group {validation.username.isInvalid && 'has-error'}">
                            <label for="username">Username</label>
                            <input type="text" className="form-control" id="username"
                                placeholder="Username" name='username' value={this.state.username}
                                onChange={this.handleInputChange} />
                            <span className="help-block">{validation.username.message}</span>

                        </div>
                        <div className="form-group {validation.password.isInvalid && 'has-error'}">
                            <label for="password">Password</label>
                            <input type="password" className="form-control"
                                id="password" placeholder="Password" name='password' value={this.state.password} onChange={this.handleInputChange} />

                            <span className="help-block">{validation.password.message}</span>
                        </div>


                        <div className="form-group">
                            <div>
                                <button type="submit" onClick={this.handleSubmit} className="btn btn-success">Register</button>

                                <span style={{ paddingLeft: 15 }}>Already have an account? 
                                <a className="btn btn-link" href="/login"> <button type="button" className="btn btn-dark" >
                                LOGIN
                    </button></a></span> 
                            </div>
                        </div>
                    </form>

                </div>


                </div>
            </div>
        )
    }
}
