import React from 'react';
import '../../assets/Styles/Styles.css';
import beauty from '../../assets/Images/beauty.jpg';
import axios from 'axios';
import env from "../../env";
import { Link } from 'react-router-dom';
import ReadMoreReact from 'read-more-react';



class HomeBody extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            loading: true,
            posts: [],
            error: null,
        }
    }

    async getPosts() {

        const response = await axios.get(`${env.api}/posts`)
        try {

            this.setState({
                posts: response.data.data,
                loading: false,

            })

        }


        catch (error) { this.setState({ error, loading: false }) }
    }

    componentDidMount() {
        this.getPosts();
    }



    render() {
        const { loading, posts } = this.state;
        let small = posts.splice(0, 3)
        return (

            <div className='container homebdy'>
                <div className='row'>
                    <div className='col-sm-6'>
                        <a href='#'><img src={beauty} alt='beauty' className='lgimg' /></a>
                        <div>
                            <h1><a href='#'>Why I Don't Look at My<br></br> Mirror When I Do Skin Care</a></h1>
                            <span className='normalText'>Despite how overwhelming skin care routines can appear, these cleansing rituals actually bring about benefits beyond glowing skin and reducing problemâ€¦</span>
                        </div>
                    </div>
                    <div className='col-sm-6 '>
                        <React.Fragment>

                            {!loading ? (small.map(post => {

                                const { image_url, title, _id } = post;
                                return (
                                    <p>
                                        <div className='media' key={_id}>

                                            <img src={image_url} className='mr-3 smimg' />
                                            <div className='media-body'>
                                                <h5 className='mt-0' >{title}</h5>

                                                <p>
                                                    <Link to={"/posts/" + _id}>
                                                        <button type="submit"
                                                            className="btn btn-success readmorebtn" >READ POST</button>
                                                    </Link>

                                                </p>
                                            </div>

                                            <hr />

                                        </div>
                                    </p>
                                )
                            })) : (
                                    <p>Loading</p>
                                )
                            }

                        </React.Fragment>
                    </div>

                </div>

                <div className='ltst'><span><i>Latest Stories</i></span></div>
                <hr className='hr' />

                <div >
                    <React.Fragment>
                        <div className='row'>
                            <div className='col-sm-10'>
                                <div className='text '>
                                    {!loading ? (this.state.posts.map(post => {

                                        const { image_url, author, title, body, _id } = post;
                                        return (
                                            <div className='dis d' key={_id}>
                                                <div  ><img src={image_url} className='mdimg' /> </div>
                                                <div className='text'>
                                                    <h3>{title}</h3>

                                                    <p className=''>
                                                        <Link to={"/posts/" + _id}>
                                                            <button type="submit"
                                                                className="btn btn-success readmorebtn" >READ POST</button>
                                                        </Link>

                                                    </p>

                                                </div>

                                                <hr />
                                            </div>
                                        )
                                    })) : (
                                            <p>Loading</p>
                                        )
                                    }
                                </div>

                            </div>

                        </div>

                    </React.Fragment>


                </div>
            </div>
        );
    }
}

export default HomeBody
